        this.$button.on(events.click, $.proxy(function() {

            var show = (!this.$element.hasClass('state-open'));

            if(show) {

                this.$button.trigger(events.focus);

                this.open();
            }
            else {

                this.close();
            }


        }, this));

        this.$cancelBtn.on(events.click, $.proxy(function() {

            this.close();

        }, this));

